    --hover-background-color: rgba(47, 49, 54, 0.85);
    --text-color: #ffffff;
    --box-shadow-color: rgba(0, 0, 0, 0.3);
    --backdrop-filter: blur(10px);
    --spacing: 20px;
    --padding: 15px;
    --channel-padding: 12px;
    --guild-padding: 12px;
    --member-padding: 12px;
    --message-padding: 12px;
    --input-padding: 12px;
    --button-padding: 12px;
